import styled from 'styled-components';

const Container = styled.div`
  display:flex;
  flex-direction:column;
`;

const Header = styled.div`
  display:flex;
  flex-direction:row;
  background-color:black;
  color:white;
  padding:10px;
  font-size:25px;
  font-weight:bold;
  box-shadow:0 3px 6px 0 #555;
`;

const AppName= styled.div`
  dislay:flex;
  flex-direction:row;
  align-items:center;
`;

const MovieImage = styled.img`
  width:45px;
  height:45px;
  margin:15px;
`;

function App() {
  return (
      <Container>
        <Header>
          <AppName>
            <MovieImage src="./movie.png"/>
            React Movie App
            </AppName>
        </Header>
        Movie App
        </Container>
  );
}

export default App;
