import styled from 'styled-components';
import Axios from "axios";
import { useEffect, useState } from 'react';
const API_KEY = "ec479189";

const Container =styled.div`
display: flex;
flex-direction: row;
padding: 20px 30px;
justify-content: center;
border-bottom: 1px solid lightgray;
`;

const CoverImage = styled.img`
object-fit: cover;
height: 350px;
`;
const Moviename = styled.span`
font-size: 22px;
  font-weight: 600;
  color: black;
  margin: 15px 0;
  white-space: nowrap;
  overflow: hidden;
  text-transform: capitalize;
  text-overflow: ellipsis;
  & span {
    opacity: 0.8;
  }
`;


const InfoColumn = styled.div`
  display:flex;
  flex-direction:row;
  justify-content:space-between;
  `;

const MovieInfo = styled.span`
font-size: 16px;
  font-weight: 500;
  color: black;
  overflow: hidden;
  margin: 4px 0;
  text-transform: capitalize;
  text-overflow: ellipsis;
  & span {
    opacity: 0.5;
  }
`;

const MovieInfoComponent = (props)=> {
    const [movieInfo, setMovieInfo] = useState;
    const {selectedMovie} = props;
    useEffect(()=>{Axios.get(`https://www.omdbapi.com/?i=${selectedMovie}&apikey=${API_KEY}`).then((response)=>setMovieInfo(response.data)); }, [selectedMovie]);
    return (
            <Container>
                <CoverImage src={movieInfo?.Poster}/>
                <InfoColumn>
                <Moviename>Movie: KGF</Moviename>
                <MovieInfo>
                    IMDB rating:
                </MovieInfo>
                </InfoColumn>
            </Container>
    );
  };
  
export default MovieInfoComponent;
