import React from 'react'
import styled from 'styled-components';

const MovieContainer = styled.div`
  display:flex;
  flex-direction:column;
  padding:10px;
  width:280px;
  box-shadow:0 3px 10px 0 #aaa;
  cursor:pointer;
`;

const CoverImage = styled.img`
  object-fit:cover;
  height:362px;
`;

const MovieName = styled.span`
  font-size:18px;
  font-weight:600;
  color:black;
  margin:15px 0;
  white-space:no-wrap;
  overflow:hidden;
  text-overflow:ellipsis;
`;



const InfoColumn = styled.div`
  display:flex;
  flex-direction:row;
  justify-content:space-between;
  `;

const MovieInfo = styled.span`
  font-size:16px;
  font:weight:500;
  color:black;
  text-transform:capitalize;
  `;
const MovieComponents = (props)=> {
  return  <MovieContainer>
                  <CoverImage src="https://moviegalleri.net/wp-content/uploads/2019/12/Hero-Yash-KGF-Chapter-2-First-Look-Poster-HD.jpg"/>
                  <MovieName>KGF2</MovieName>
                  <InfoColumn>
                    <MovieInfo>Type: Action</MovieInfo>
                    <MovieInfo>Year: 2022</MovieInfo>
                  </InfoColumn>
          </MovieContainer>
};

export default MovieComponents
