import React from "react";
import styled from 'styled-components';

const Container =styled.div`
    display:flex;
    flex-direction:row;
    padding:20px 30px;
    justify-content:left;
    border-bottom:1px solid lightray;
`;

const CoverImage = styled.img`
  object-fit:cover;
  height:362px;
`;
const Moviename = styled.span`
font-size:18px;
font-weight:600;
color:black;
margin:15px 0;
white-space:no-wrap;
overflow:hidden;
text-overflow:ellipsis;
`;

const MovieInfo = styled.span`
font-size:16px;
font-weight:600;
color:black;
margin:15px 0;
white-space:no-wrap;
overflow:hidden;
text-overflow:ellipsis;
`;

const MovieInfoComponent = (props)=> {
    return (
            <Container>
                <CoverImage src="https://images.unsplash.com/photo-1453728013993-6d66e9c9123a?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8dmlld3xlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60"></CoverImage>
                <Moviename>Movie: KGF</Moviename>
            </Container>
    );
  };
  
export default MovieInfoComponent;
