import styled from 'styled-components';
import Axios from "axios";
import { useEffect, useState } from 'react';
const API_KEY = "ec479189";

const Container =styled.div`
    display:flex;
    flex-direction:row;
    padding:20px 30px;
    justify-content:center;
    border-bottom:1px solid lightray;
`;

const CoverImage = styled.img`
  object-fit:cover;
  height:362px;
`;
const Moviename = styled.span`
font-size:18px;
font-weight:600;
color:black;
margin:15px 0;
white-space:no-wrap;
overflow:hidden;
text-tranform:capitalize;
text-overflow:ellipsis;
`;


const InfoColumn = styled.div`
  display:flex;
  flex-direction:row;
  justify-content:space-between;
  `;

const MovieInfo = styled.span`
font-size:16px;
font-weight:500;
color:black;
margin:4px 0;
overflow:hidden;
text-tranform:capitalize;
text-overflow:ellipsis;
`;

const MovieInfoComponent = (props)=> {
    const [movieInfo, setMovieInfo] = useState;
    const {selectedMovie} = props;
    useEffect(()=>{Axios.get(`https://www.omdbapi.com/?i=${selectedMovie}&apikey=${API_KEY}`).then((response)=>setMovieInfo(response.data)); }, [selectedMovie]);
    return (
            <Container>
                <CoverImage src={movieInfo?.Poster}></CoverImage>
                <InfoColumn>
                <Moviename>Movie: KGF</Moviename>
                <MovieInfo>
                    IMDB rating:
                </MovieInfo>
                </InfoColumn>
            </Container>
    );
  };
  
export default MovieInfoComponent;
