import styled from 'styled-components';
import MovieComponents from './components/MovieComponents';
const Container = styled.div`
  display:flex;
  flex-direction:column;
`;

const Header = styled.div`
  display:flex;
  justify-content:space-between;
  flex-direction:row;
  background-color:black;
  color:white;
  align-items:center;
  padding:10px;
  font-size:25px;
  font-weight:bold;
  box-shadow:0 3px 6px 0 #555;
`;

const AppName= styled.div`
  dislay:flex;
  flex-direction:row;
  align-items:center;
  justify-content:center;
`;

const MovieImage = styled.img`
  width:45px;
  height:45px;
  margin:15px;
`;

const SearchBox = styled.div`
  display:flex;
  flex-direction:row;
  padding:10px 10px;
  background-color:white;
  border-radius:6px;
  margin-left:20px;
  width:50%;
  align-items:center;
`;
const SearchImg=styled.img`
  width:32px;
  height:32px;
  `;

const SearchInput = styled.input`
  color:black;
  font-size:16px;
  font-weight:bold;
  border:none;
  outline:none;
  margin-left:15px
`;

const MovieListContainer = styled.div`
  display:flex;
  flex-direction:row;
  flex-wrap:wrap;
  padding:30px;
  justify-content:space-evenly;
`;



function App() {
  return (
      <Container>
        <Header>
          <AppName>
            <MovieImage src="/movie-app/src/shake.svg"/>
            React Movie App
            </AppName>
            <SearchBox>
                <SearchImg src="/shrine.svg"/>
                <SearchInput placeholder="Movie Name" />
            </SearchBox>
        </Header>
          <MovieListContainer>
            <MovieComponents></MovieComponents>
            <MovieComponents></MovieComponents>
          </MovieListContainer>
        </Container>
  );
}

export default App;
