import React from 'react';
import styled from 'styled-components';
import { useState } from 'react';
import MovieComponent from './components/MovieComponent';
import Axios from 'axios';
import MovieInfoComponent from './components/MovieInfoComponent';
import Particles from "react-tsparticles";

const particlesInit = (main) => {
  console.log(main);

  // you can initialize the tsParticles instance (main) here, adding custom shapes or presets
};
  const particlesLoaded = (container) => {
  console.log(container);
};

const API_KEY = "ec479189";
const Container = styled.div`
  display:flex;
  flex-direction:column;
`;



const Header = styled.div`
  display:flex;
  justify-content:space-between;
  flex-direction:row;
  background-color:black;
  color:white;
  align-items:center;
  padding:10px;
  font-size:25px;
  font-weight:bold;
  box-shadow:0 3px 6px 0 #555;
`;

const AppName= styled.div`
  dislay:flex;
  flex-direction:row;
  align-items:center;
  justify-content:center;
`;

const MovieImage = styled.img`
  width:45px;
  height:45px;
  margin:15px;
`;

const SearchBox = styled.div`
  display:flex;
  flex-direction:row;
  padding:10px 10px;
  background-color:white;
  border-radius:6px;
  margin-left:20px;
  width:50%;
  align-items:center;
`;
const SearchImg=styled.img`
  width:32px;
  height:32px;
  `;

const SearchInput = styled.input`
  color:black;
  font-size:16px;
  font-weight:bold;
  border:none;
  outline:none;
  margin-left:15px
`;

const MovieListContainer = styled.div`
  display:flex;
  flex-direction:row;
  flex-wrap:wrap;
  padding:30px;
  justify-content:space-evenly;
`;



function App() {

  const [searchQuery,updateSearchQuery] = useState();
  const [timeoutID, updatetimeoutID] = useState();
  const [movieList,updateMovielist] = useState([]);
  const [selectedMovie, onMovieSelect] = useState();

  const fetchdata = async (searchString)=>{
    const response = await Axios.get(`http://www.omdbapi.com/?s=${searchString}&apikey=${API_KEY}`
    );
    updateMovielist(response.data.Search);
  }

  const ontextChange = (event)=>{
        clearTimeout(timeoutID);
        updateSearchQuery(event.target.value);
        const timeout = setTimeout(()=> fetchdata(event.target.value),500);
        updatetimeoutID(timeout);
  }



  return (
    <div className="App">
    <Particles
    id="tsparticles"
    className='particles'
    init={particlesInit}
    loaded={particlesLoaded}
    options={{
      background: {
        color: {
          value: "",
        },
      },
      fpsLimit: 60,
      interactivity: {
        events: {
          onClick: {
            enable: true,
            mode: "push",
          },
          onHover: {
            enable: true,
            mode: "repulse",
          },
          resize: true,
        },
        modes: {
          bubble: {
            distance: 400,
            duration: 2,
            opacity: 0.8,
            size: 40,
          },
          push: {
            quantity: 4,
          },
          repulse: {
            distance: 100,
            duration: 0.4,
          },
        },
      },
      particles: {
        color: {
          value: "#ffffff",
        },
        links: {
          color: "#ffffff",
          distance: 150,
          enable: true,
          opacity: 0.5,
          width: 1,
        },
        collisions: {
          enable: true,
        },
        move: {
          direction: "none",
          enable: true,
          outMode: "bounce",
          random: false,
          speed: 2.5,
          straight: false,
        },
        number: {
          density: {
            enable: true,
            area: 800,
          },
          value: 80,
        },
        opacity: {
          value: 0.5,
        },
        shape: {
          type: "circle",
        },
        size: {
          random: true,
          value: 5,
        },
      },
      detectRetina: true,
    }}
  />
      <Container>
        <Header>
          <AppName>
            <MovieImage src="/movie-app/src/shake.svg"/>
            React Movie App
            </AppName>
            <SearchBox>
                <SearchImg src="/shrine.svg"/>
                <SearchInput placeholder="Movie Name" value={searchQuery} onChange={ontextChange}/>
            </SearchBox>
        </Header>
        {selectedMovie && <MovieInfoComponent selectedMovie={selectedMovie}/>}
          <MovieListContainer>
          {movieList?.length ? (
          movieList.map((movie, index) => (
            <MovieComponent
              key={index}
              movie={movie}
              onMovieSelect={onMovieSelect}
            />
          ))
        ) : "NO RESULT"}
          </MovieListContainer>
        </Container>
       </div>
  );
}

export default App;
